import{_ as e,c as a,o as i,a4 as r}from"./chunks/framework.BHBXlUku.js";const p=JSON.parse('{"title":"机器学习基础","description":"","frontmatter":{},"headers":[],"relativePath":"ai/ml-basics.md","filePath":"ai/ml-basics.md","lastUpdated":1744343656000}'),n={name:"ai/ml-basics.md"},l=r('<h1 id="机器学习基础" tabindex="-1">机器学习基础 <a class="header-anchor" href="#机器学习基础" aria-label="Permalink to &quot;机器学习基础&quot;">​</a></h1><h2 id="什么是机器学习" tabindex="-1">什么是机器学习？ <a class="header-anchor" href="#什么是机器学习" aria-label="Permalink to &quot;什么是机器学习？&quot;">​</a></h2><p>机器学习（Machine Learning, ML）是人工智能的一个分支，它使计算机系统能够从数据中学习并改进，而无需进行显式编程。</p><h2 id="主要类型" tabindex="-1">主要类型 <a class="header-anchor" href="#主要类型" aria-label="Permalink to &quot;主要类型&quot;">​</a></h2><h3 id="_1-监督学习-supervised-learning" tabindex="-1">1. 监督学习 (Supervised Learning) <a class="header-anchor" href="#_1-监督学习-supervised-learning" aria-label="Permalink to &quot;1. 监督学习 (Supervised Learning)&quot;">​</a></h3><ul><li><strong>概念</strong>：使用带有标签的数据集进行训练，模型学习输入与输出之间的映射关系。</li><li><strong>例子</strong>：图像分类、垃圾邮件检测、房价预测。</li><li><strong>常见算法</strong>： <ul><li>线性回归 (Linear Regression)</li><li>逻辑回归 (Logistic Regression)</li><li>支持向量机 (Support Vector Machine, SVM)</li><li>决策树 (Decision Tree)</li><li>随机森林 (Random Forest)</li></ul></li></ul><h3 id="_2-无监督学习-unsupervised-learning" tabindex="-1">2. 无监督学习 (Unsupervised Learning) <a class="header-anchor" href="#_2-无监督学习-unsupervised-learning" aria-label="Permalink to &quot;2. 无监督学习 (Unsupervised Learning)&quot;">​</a></h3><ul><li><strong>概念</strong>：使用没有标签的数据集进行训练，模型自行发现数据中的结构和模式。</li><li><strong>例子</strong>：聚类分析、降维、异常检测。</li><li><strong>常见算法</strong>： <ul><li>K-均值聚类 (K-Means Clustering)</li><li>主成分分析 (Principal Component Analysis, PCA)</li><li>DBSCAN</li></ul></li></ul><h3 id="_3-强化学习-reinforcement-learning" tabindex="-1">3. 强化学习 (Reinforcement Learning) <a class="header-anchor" href="#_3-强化学习-reinforcement-learning" aria-label="Permalink to &quot;3. 强化学习 (Reinforcement Learning)&quot;">​</a></h3><ul><li><strong>概念</strong>：智能体（Agent）通过与环境交互学习，根据获得的奖励或惩罚来优化其行为策略。</li><li><strong>例子</strong>：游戏 AI（如 AlphaGo）、机器人控制、推荐系统。</li><li><strong>关键概念</strong>：状态、动作、奖励、策略、价值函数。</li></ul><h2 id="常用工具库" tabindex="-1">常用工具库 <a class="header-anchor" href="#常用工具库" aria-label="Permalink to &quot;常用工具库&quot;">​</a></h2><ul><li><strong>Scikit-learn</strong>: Python 中最流行的通用机器学习库。</li><li><strong>TensorFlow</strong>: Google 开发的端到端开源机器学习平台。</li><li><strong>PyTorch</strong>: Facebook 开发的开源机器学习库，以其灵活性和易用性著称。</li></ul><h2 id="模型评估" tabindex="-1">模型评估 <a class="header-anchor" href="#模型评估" aria-label="Permalink to &quot;模型评估&quot;">​</a></h2><ul><li><strong>分类问题</strong>：准确率、精确率、召回率、F1 分数、AUC-ROC 曲线。</li><li><strong>回归问题</strong>：均方误差 (MSE)、均方根误差 (RMSE)、平均绝对误差 (MAE)。</li></ul><p>更多内容持续更新中...</p>',15),t=[l];function o(s,c,h,d,g,u){return i(),a("div",null,t)}const m=e(n,[["render",o]]);export{p as __pageData,m as default};
